mapper.select:
select userID, name, email, post, addr1, addr2, phone
from member
where userID=#{userID}

mapper.update:
update member 
set name=#{name}, email=#{email}, post=#{post}, addr1=#{addr1}, addr2=#{addr2}, phone=#{phone}
where userID=#{userID}

mapper.select:
select userid, sdate, edate, traveltitle, areacode
from TRAVEL
where userid =#{userid}
